frappe.ui.form.on('Timesheet', {
    onload: function(frm) {
        frm.trigger('update_overtime_fields');
        frm.trigger('check_holidays');
    },

    validate: function(frm) {
        frm.trigger('update_overtime_fields');
        frm.trigger('check_holidays');
    },

    update_overtime_fields: function(frm) {
        const max_rt_hours = 9 + (30 / 60); // 9 Hours 30 Minutes in decimal hours: 9.5

        frm.doc.time_logs.forEach(function(row) {
            if (row.hours) {
                const total_hours = parseFloat(row.hours); // Convert hours to a decimal number

                frappe.call({
                    method: 'frappe.client.get',
                    args: {
                        doctype: 'Employee',
                        name: frm.doc.employee // Ensure this field is correctly referenced
                    },
                    callback: function(r) {
                        if (r.message) {
                            const employee = r.message;
                            if (employee.custom_applicable_for_overtine) {
                                row.custom_ot_1 = total_hours > max_rt_hours ? total_hours - max_rt_hours : 0;
                            } else {
                                row.custom_ot_1 = 0;
                            }
                            frm.refresh_field('time_logs'); // Refresh only the affected row
                        }
                    }
                });
            } else {
                row.custom_ot_1 = 0;
            }
        });

        // Refresh the child table to ensure all fields are updated
        frm.refresh_field('time_logs');
    },

    check_holidays: function(frm) {
        // Fetch employee record first
        frappe.call({
            method: 'frappe.client.get',
            args: {
                doctype: 'Employee',
                name: frm.doc.employee // Ensure this field is correctly referenced
            },
            callback: function(r) {
                if (r.message) {
                    const employee = r.message;
                    const holiday_list_name = employee.holiday_list;
                    console.log("Holiday List Name:", holiday_list_name); // Debugging line

                    if (holiday_list_name) {
                        frappe.call({
                            method: 'frappe.client.get',
                            args: {
                                doctype: 'Holiday List',
                                filters: { name: holiday_list_name },
                                fields: ['holidays']
                            },
                            callback: function(r) {
                                console.log("Holiday List Data:", r.message);

                                if (r.message && r.message.holidays) {
                                    const holidays = r.message.holidays;

                                    frm.doc.time_logs.forEach(function(row) {
                                        if (row.from_time) {
                                            const from_time = new Date(row.from_time);
                                            const from_time_date = from_time.toISOString().split('T')[0]; // Convert to YYYY-MM-DD

                                            console.log("From Time Date:", from_time_date);

                                            let holiday_found = false;
                                            holidays.forEach(function(holiday) {
                                                const holiday_date = new Date(holiday.holiday_date).toISOString().split('T')[0]; // Convert to YYYY-MM-DD
                                                console.log("Checking Holiday Date:", holiday_date);
                                                if (holiday_date === from_time_date) {
                                                    holiday_found = true;
                                                    console.log("Holiday Found:", holiday_date);
                                                }
                                            });

                                            if (holiday_found) {
                                                if (employee.custom_applicable_for_overtine) {
                                                    row.custom_ot_2 = row.hours; // Set to hours field value
                                                    row.custom_ot_1 = 0; // Clear custom_ot_1 if custom_ot_2 is set
                                                    console.log("Setting custom_ot_2:", row.custom_ot_2);
                                                } else {
                                                    row.custom_ot_2 = 0;
                                                }
                                                frm.refresh_field('time_logs'); // Refresh only the affected row
                                            }
                                        }
                                    });

                                    // Refresh the child table to ensure all fields are updated
                                    frm.refresh_field('time_logs');
                                } else {
                                    console.error("No holidays found for the specified Holiday List.");
                                }
                            }
                        });
                    } else {
                        console.error("Holiday list name not found in the employee record.");
                    }
                }
            }
        });
    }
});

// Client script for the Timesheet Detail child table
frappe.ui.form.on('Timesheet Detail', {
    hours: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];
        if (row.hours) {
            frm.trigger('update_overtime_fields');
            frm.trigger('check_holidays');
        }
    },

    from_time: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];
        if (row.from_time) {
            frm.trigger('check_holidays');
        }
    }
});

frappe.ui.form.on('Sales Invoice', {
    customer: function(frm) {
        if (frm.doc.customer) {
            frappe.call({
                method: 'frappe.client.get_list',
                args: {
                    doctype: 'Program Enrollment',
                    filters: { student_name: frm.doc.customer_name },
                    fields: ['program', 'academic_year'],
                    limit_page_length: 1
                },
                callback: function(response) {
                    if (response.message && response.message.length > 0) {
                        let enrollment = response.message[0];
                        frm.set_value('custom_grade', enrollment.program);
                        frm.set_value('custom_academic_year', enrollment.academic_year);
                    } else {
                        frappe.msgprint(__('No matching Program Enrollment found.'));
                    }
                }
            });
        } else {
            frm.set_value('custom_grade', '');
            frm.set_value('custom_academic_year', '');
        }
    }
});

