function check_and_update_enrollment(frm) {
    // ØªØ­Ù‚Ù‚ Ù…Ù† ÙˆØ¬ÙˆØ¯ Ù‚ÙŠÙ…Ø© ÙÙŠ party_name
    if (frm.doc.party && frm.doc.party_name) {
        frappe.call({
            method: 'frappe.client.get_list',
            args: {
                doctype: 'Program Enrollment',
                filters: { student_name: frm.doc.party_name },
                fields: ['program', 'academic_year'],
                limit_page_length: 1
            },
            callback: function(response) {
                if (response.message && response.message.length > 0) {
                    let enrollment = response.message[0];
                    let grade_changed = frm.doc.custom_grade !== enrollment.program;
                    let year_changed = frm.doc.custom_academic_year !== enrollment.academic_year;

                    // Ø­Ø¯Ù‘Ø« ÙÙ‚Ø· Ø¥Ø°Ø§ ÙÙŠÙ‡ Ø§Ø®ØªÙ„Ø§Ù
                    if (grade_changed || year_changed) {
                        frm.set_value('custom_grade', enrollment.program);
                        frm.set_value('custom_academic_year', enrollment.academic_year);
                    }
                }
            }
        });
    }
}

