                const existing_students = existing_invoices.map(inv => inv.student);
                let created_count = 0;
                let skip_message_shown = false;

                let promises = students_to_process.map(student_row => {
                    return frappe.db.get_doc('Student', student_row.student)
                        .then(student_doc => {
                            if (!student_doc.customer) {
                                frappe.msgprint(`ğŸš« Ù„Ø§ ÙŠÙˆØ¬Ø¯ Ø¹Ù…ÙŠÙ„ Ù„Ù„Ø·Ø§Ù„Ø¨ ${student_row.student}`);
                                return null;
                            }

                            if (existing_students.includes(student_row.student)) {
                                if (!skip_message_shown) {
                                    frappe.msgprint("âš ï¸ ÙŠÙˆØ¬Ø¯ ÙØ§ØªÙˆØ±Ø© Ø³Ø§Ø¨Ù‚Ø© Ù„Ù†ÙØ³ Ø§Ù„Ø·Ø§Ù„Ø¨ ÙˆØ¬Ø¯ÙˆÙ„ Ø§Ù„Ø±Ø³ÙˆÙ…");
                                    skip_message_shown = true;
                                }
